import React from 'react'
import styles from "./WeatherBoard.module.css"
import HoursBar from './HoursBar'
import MainBoard from './MainBoard'
import DaysBar from './DaysBar'

class WeatherBoard extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            isLoaded : false,
            isHourlyReady: false,
            activeBoard : "current",
            activeDay : 0,
            activeHour : 0,
            forecast : null
        }
    }

    async componentDidMount(){
        try{
            const lat = '48.450001'
            const lon = '34.983334'
            const url = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&units=metric&exclude=minutely&appid=28fac3f57b86f68e027fe7277b3bf432`
            let response = await fetch(url)
            if(response.ok){
                const forecast = await response.json()
                this.setState({
                    forecast : forecast,
                    isLoaded : true, 
                })

                this.refactorHourlyForecast()

            }else{
                console.log(Error);
            }

        }catch(e){
            console.log(e);
        }
    }

    handleDayClick = dayIndex => {
        this.setState({
            activeBoard:"daily",
            activeDay:dayIndex
        })
    }

    refactorHourlyForecast = () => {
        const forecast = this.state.forecast
        const hourlyForecast = forecast.hourly

        const currentHour = new Date(hourlyForecast[0].dt * 1000).getHours()

        const today = []

        for(let i = 0; i < 24; i++){
            if(i < currentHour){
                today.push(null)
            }else{
                today.push(hourlyForecast[i - currentHour])
            }
        }
        console.log(today);
        today.forEach(hour => {
            console.log(hour);
        })

        const tomorrow = []

        for(let i = 0; i < 24; i++){
            tomorrow.push(hourlyForecast[24 - currentHour + i])
        }

        console.log(tomorrow);

        const afterDay = []

        for(let i = 0; i < 24; i++){
            if(i < 24 - (24 - currentHour)){
                afterDay.push(hourlyForecast[24 + 24 - currentHour - 1])
            }else{
                afterDay.push(null)
            }

        }

        console.log(afterDay);

    }

    render(){
        return(
            this.state.isLoaded
            ? <div className={styles.WeatherBoard}>
                <MainBoard
                    forecast={this.state.forecast}
                    activeBoard={this.state.activeBoard}
                    activeDay={this.state.activeDay}
                    activeHour={this.state.activeHour}
                />
                <HoursBar/>
                <DaysBar
                    days={this.state.forecast.daily}
                    onDayClick={this.handleDayClick}
                />
            </div> 
            : <p>loading forecast</p>
        )
    }

}

export default WeatherBoard
