import React from 'react'
import styles from './HoursBar.module.css'

const HoursBar = props => {


    let forecast = []

    if(props.activeDay === 0){
        forecast=props.forecast.today
    }else if(props.activeDay === 1){
        forecast=props.forecast.tomorrow
    }else if(props.activeDay === 2){
        forecast=props.forecast.afterDay
    }else{
        forecast=null
    }

    const dayCells = []
    for(let i = 0; i < 12; i++){
        dayCells.push(
            <td key={i} className={styles.Hour}>

            </td>
        )
    }

    const nightCells = []
    for(let i = 12; i < 24; i++){
        nightCells.push(
            <td key={i} className={styles.Hour}>

            </td>
        )
    }

    forecast = null

    return(
        forecast
        ? <div className={styles.HoursBar}>
            <table className={styles.Table}>
                <tbody>
                <tr>
                    {dayCells}
                </tr>
                <tr>
                    {nightCells}
                </tr>
                </tbody>
            </table>
         </div>
        : <div className={styles.HoursBarHidden}>
            <h2>Hourly forecast is not avaible for now</h2>
         </div>

    )

}

export default HoursBar
