import React from 'react'

class WeatherBoard extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            forecast : null
        }
    }

    async componentDidMount(){
        try{
            const lat = '50.45466'
            const lon = '30.5238'
            const url = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&exclude=minutely&appid=28fac3f57b86f68e027fe7277b3bf432`
            let response = await fetch(url)
            if(response.ok){
                const forecast = response.json()
                this.setState({
                    forecast : forecast
                })
                console.log(this.state.forecast); 
            }else{
                console.log(Error);
            }

        }catch(e){
            console.log(e);
        }
    }




    render(){
        return(<p>Weather</p>)
    }

}

export default WeatherBoard
