import React from 'react'
import DaysBar from './DaysBar'
import MainBoard from './MainBoard'

class WeatherBoard extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            isLoaded : false,
            chosenDay : 0,
            forecast : null
        }
    }

    async componentDidMount(){
        try{
            const lat = '48.450001'
            const lon = '34.983334'
            const url = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&exclude=minutely&appid=28fac3f57b86f68e027fe7277b3bf432`
            let response = await fetch(url)
            if(response.ok){
                const forecast = await response.json()
                this.setState({
                    forecast : forecast,
                    isLoaded : true  
                })
            }else{
                console.log(Error);
            }

        }catch(e){
            console.log(e);
        }
    }

    handleDayClick = dayId => {
        this.setState({
            chosenDay: dayId
        })
    }


    render(){
        return(
            this.state.isLoaded
            ? <div style={{display:"flex", justifyContent:"center"}}>
                <MainBoard day = {this.state.forecast.daily[this.state.chosenDay]}/>
                <DaysBar
                    days = {this.state.forecast.daily}
                    onDayClick = {this.handleDayClick}
                />
            </div> 
            : <p>loading forecast</p>
        )
    }

}

export default WeatherBoard
