import React from 'react'

const DaysBar = props => {
    const days = props.days

    handleClick = dayIndex => {
        props.onDayClick(dayIndex)
    }

    return(
        <ul style={{width: "300px", heigth: "300px", background: "Chartreuse", padding: "30px"}}>
            {days.map((day, index) => {
                return <li
                        key={day.dt}
                        onClick={this.handleClick()}
                       >
                           {new Date(day.dt * 1000).toDateString()}
                       </li>
            })}
        </ul>
    )
}

export default DaysBar
