import React from 'react'
import styles from './MainBoard.module.css'
import HoursBar from './HoursBar'

const MainBoard = props => {
    let forecast

    switch(props.activeBoard){
        case "current":
            forecast = props.forecast.current
            break
        case "hourly":
            if(props.hourlyForecastType === "today"){
                forecast = props.forecast.hourly.today[props.activeHour]
            }else if(props.hourlyForecastType === "tomorrow"){
                forecast = props.forecast.hourly.tomorrow[props.activeHour]
            }else{
                forecast = props.forecast.hourly.afterDay[props.activeHour]
            }
            break
        case "daily":
            forecast = props.forecast.daily[props.activeDay]
            break
        default:
            forecast = props.forecast.current
    }

    return(
        <div className={styles.MainBoard}>
            <div> 
                {props.activeDay === 0 ? <h1>Today</h1> : null}
                {props.activeDay === 1 ? <h1>Tomorrow</h1> : null}
                {props.activeDay === 2 ? <h1>After Day</h1> : null}
                <h1>{new Date(forecast.dt * 1000).toDateString()}</h1>
                <h1>{forecast.temp.min.toFixed(0)} - {forecast.temp.max.toFixed(0)}C</h1> 
            </div>

            {
                props.isHourlyReady && props.isHourlyRequired
                ? 
                <HoursBar
                forecast={props.forecast}
                activeDay={props.activeDay}
                />
                : <div></div>
            }           

        </div>
    )
}

export default MainBoard
